import React, { useState, useEffect } from 'react'
import "./Timer.css";

export default function Timer(props) {
    const [progressState, setprogressState] = useState(100)
    let time = props.time
    useEffect(() => {        
        let timer = setTimeout(() => {
            updateProgress(props.time)
        }, 100)
        if (progressState <= 0) {
            clearTimeout(timer)
            props.settimerState('finished');
        } else if (props.time === 'pauseTimer') {
            clearTimeout(timer)     
        }
    },[progressState]);

    function updateProgress(value) {
        setprogressState(progressState - value)
    }

    return (
        <div>
            <div id="progressbar">
                <div style={{ width: progressState + '%' }}></div>
            </div>            
        </div>

    )
}
