import React, { useEffect } from 'react';
import { connect } from "react-redux";
import { Route } from 'react-router-dom';
import { heatupAC, trainingAC } from "./redux/actions/actions";

import Header from './components/Header/Header';
import Heatup from './components/Heatup/Heatup'
import StratAndPauseButton from './components/StratAndPauseButton/StratAndPauseButton'

const mapStateToProps = (state) => ({
  state
});

function App(props) {
  let appState = props.state.training.status;
  let heatup = props.heatup;

  return (
    <div className='App'>
      <Header state={appState} />
      <Route path='/Heatup' render={(props) => <Heatup heatup={heatup} fetchAdress={'/exercises/heatup'}/>} exact />
      <Route path='/Training' render={(props) => <Heatup heatup={heatup} fetchAdress={'/exercises/training'}/>} exact />
      {/* Route на тренировку */}
      {/* Route на оценку */}
      <Route path='/' component={StratAndPauseButton} exact />
    </div>
  );
}

const mapDispatchToProps = dispatch => {
  return {
    heatup: (exercise) => { dispatch(heatupAC(exercise)) },
    training: (exercise) => { dispatch(trainingAC(exercise)) },
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(App);
