import React, { useState, useEffect } from 'react'
import "./Timer.css";

export default function Timer(props) {
    const [progressState, setprogressState] = useState(100)
    const [timerState, settimerState] = useState('active')
    const [timeState, settimeState] = useState(props.time)

    useEffect(() => {        
        let timer = setTimeout(() => {
            updateProgress(timeState)
            settimerState('active')
        }, 100)
        if (progressState < 0) {
            clearTimeout(timer)
            settimerState('finished')
            props.settimerState('finished');
        } else if (timeState === 'pauseTimer') {
            clearTimeout(timer)            
            settimerState('paused')
        }
    });

    function updateProgress(value) {
        setprogressState(progressState - value)
    }

    return (
        <div>
            <div id="progressbar">
                <div style={{ width: progressState + '%' }}></div>
            </div>            
        </div>

    )
}
