import React, { useState, useEffect } from 'react';
import { connect } from "react-redux";
import './Heatup.css'
import { heatupAC } from "../../redux/actions/actions";

import ExerciseContainer from '../ExerciseContainer/ExerciseContainer'

import Neck from '../HeatUpImg/neck.jpg'
import Hands from '../HeatUpImg/hands.jpg'
import Test from '../HeatUpImg/ZIyE.gif'

function Heatup(props) {
    const [heatupExercises, setHeatupExercises] = useState(null)  

    useEffect(()=>{
        getHeatup() 
    },[props])

    async function getHeatup() {
        let res = await fetch('/exercises/heatup');
        let heatupExercises = await res.json();
        console.log(heatupExercises)
        setHeatupExercises(heatupExercises) ;
    }   
    // async function create() {
    //     let res = await fetch('/exercises/test', {
    //         method: 'POST',
    //         credentials: 'include',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify([{ name: 'Xpenb', img: Test, duration: 10 }, { name: 'Hands', img: Hands, duration: 15 },{ name: 'Neck', img: Neck, duration: 20 }])
    //     });
    // }
    // create();

    return (
        <>
            {heatupExercises && <ExerciseContainer heatup={props.heatup} heatupExercises={heatupExercises}/>}
        </>
    )
}

const mapDispatchToProps = dispatch => {
    return {
        //heatup: (exercise) => { dispatch(heatupAC(exercise)) },
    }
}


export default connect(
    null,
    null,
)(Heatup);
