import React, { useState, useEffect } from 'react';
import { connect } from "react-redux";
import './Heatup.css'

import ExerciseContainer from '../ExerciseContainer/ExerciseContainer'

function Activity(props) {
    const [heatupExercises, setHeatupExercises] = useState(null)  

    useEffect(()=>{
        getActivity() 
    },[props])

    async function getActivity() {
        let res = await fetch(props.fetchAdress);
        let heatupExercises = await res.json();
        console.log(heatupExercises)
        setHeatupExercises(heatupExercises) ;
    }   

    return (
        <>
            {heatupExercises && <ExerciseContainer setActivity={props.setActivity} heatupExercises={heatupExercises}/>}
        </>
    )
}

export default connect(
    null,
    null,
)(Activity);
