import React, { useState } from 'react'
import { NavLink } from 'react-router-dom';

import "./StratAndPauseButton.css";

export default function StratAndPauseButton(props) {
    const [state, setstate] = useState('START\nТЕSТ')

    return (
        <div>
            <div className='btnContainer' onClick={state === 'START\nТЕSТ' || state === 'Resume' ? () => {
                props.setstate("Heating Up")
                setstate('Pause')
            } : () => {
                props.setstate("Paused")
                setstate('Resume')
                props.settimerState("Paused")
            }}>
                <NavLink to="/Assesment" className="testButton">{state}</NavLink>
            </div>

        </div>

    )
}
