import React, { useState } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Header from './components/Header/Header'
import StratAndPauseButton from './components/StratAndPauseButton/StratAndPauseButton'
import Timer from './components/Timer/Timer'
import Heatup from './components/Heatup/Heatup'

function App() {
  const [state, setstate] = useState('Welcome')
  const [timerState, settimerState] = useState(null)
  console.log(timerState)
  return (
    <Router>
      <div className='App'>
        <Header state={state} />
        {state === "Heating Up" || state === "Paused"} && <Heatup />}
        {state === "Paused" && <Heatup />}
        <StratAndPauseButton setstate={setstate} settimerState = {settimerState}/>
        {timerState && <div><Timer time = {timerState} settimerState={settimerState}/></div>}        
        {/* <Route path='/Assesment' component={Assesment} exact/> */}
      </div>
    </Router>
  );
}

export default App;
