import React, { useState, useEffect } from 'react'
import "./Timer.css";

export default function Timer(props) {
    const [progressState, setprogressState] = useState(100)
    const [timerState, settimerState] = useState('100')

    useEffect(() => {        
        let timer = setTimeout(() => {
            updateProgress(props.time)
        }, 1000)
        if (progressState < 0) {
            clearTimeout(timer)
        } else if (props.time === 'stop') {
            clearTimeout(timer)
        }
    });

    function updateProgress(value) {
        setprogressState(progressState - value)
    }

    return (
        <div>
            <div id="progressbar">
                <div style={{ width: progressState + '%' }}></div>
            </div>
            {progressState < 0 && <div>WELL DONE!!!</div>}
        </div>

    )
}
