import React, { useState, useEffect } from 'react';
import Exercise from '../Exrcise/Exercise'
import './Heatup.css'
import Neck from './neck.jpg';
import Hands from './hands.jpg';

export default function Heatup(props) {
    const [exerciseNumber, setexerciseNumber] = useState(null)

    let exerciseArray = [{ name: 'Neck', img: Neck }, { name: 'Hands', img: Hands }]    

    useEffect(() => {
        props.setExerciseQuantity(exerciseArray.length-1)
    }, [exerciseArray.length])

    useEffect(() => {
        props.exerciseNumber && props.setHeatupExercise(exerciseArray[props.exerciseNumber].name)
    }, [props.exerciseNumber])

    return (
        <>            
            <div className="imgContainer">
                {exerciseArray[props.exerciseNumber] && <Exercise exercise={exerciseArray[props.exerciseNumber]} />}
            </div>
        </>
    )
}
