import React, { useState, useEffect } from 'react'
import StratAndPauseButton from './StratAndPauseButton/StratAndPauseButton'
import { heatupAC, startTimerAC, setExerciseQuantityAC } from "../redux/actions/actions";
import { connect } from "react-redux";
import {Route} from 'react-router-dom';
import Heatup from './Heatup/Heatup'
import Header from './Header/Header'

const mapStateToProps = (state) => ({
    state
});

function StartAndPauseContainer(props) {

    return (
        <div>
            <Header state={props.state.training.status} />
            <Route render={(props) => <Heatup setHeatupExercise={props.heatup} setExerciseQuantity={props.setExerciseQuantity} />} exact/>
            {props.state.training.exercise && <Heatup setHeatupExercise={props.heatup} setExerciseQuantity={props.setExerciseQuantity} />}
            <StratAndPauseButton setHeatupExercise={props.heatup} />
        </div>
    )
}

const mapDispatchToProps = dispatch => {
    return {
        heatup: (exercise) => { dispatch(heatupAC(exercise)) },
        startTimer: (seconds) => { dispatch(startTimerAC(seconds)) },
        setExerciseQuantity: (quntity) => { dispatch(setExerciseQuantityAC(quntity)) }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps,
)(StartAndPauseContainer);
