import React, { useState } from 'react'
import {NavLink} from 'react-router-dom';
import "./StratAndPauseButton.css";

export default function StratAndPauseButton(props) {
    const [state, setstate] = useState('START\nТЕSТ')

    return (
        <div className = 'btnContainer' onClick = {state === 'START\nТЕSТ' ? () => {
            props.setstate("Heating Up")
            setstate('Pause')
        } : () => {
            props.setstate("P")
            setstate('START\nТЕSТ')
        }}>
            <NavLink to="/Assesment" className="testButton">{state}</NavLink>
        </div>
    )
}
