import React, { useState, useEffect } from 'react';
import './Heatup.css'

import ExerciseContainer from '../ExerciseContainer/ExerciseContainer'
import Neck from '../HeatUpImg/neck.jpg'
import Hands from '../HeatUpImg/hands.jpg'
import ZIyE from '../HeatUpImg/ZIyE.gif'

export default function Activity(props) { 
    //const [activityExercises, setActivityExercises] = useState([{ name: "Xpenb", img: "/static/media/ZIyE.f3181bd4.gif", duration: "10", __v: 0},{ name: "Hands", img: "/static/media/hands.8172b8f8.jpg", duration: "15", __v: 0},{ name: "Neck", img: "/static/media/neck.5f37f58f.jpg", duration: "20", __v: 0}])  
    const [activityExercises, setActivityExercises] = useState(false)
    useEffect(()=>{
        getActivity() 
    },[props])

    async function getActivity() {
        let res = await fetch(props.fetchAdress);
        let activityExercises = await res.json();
        setActivityExercises(activityExercises) ;
        console.log(activityExercises)
    }   

    return (
        <>
            {activityExercises && <ExerciseContainer setActivity={props.setActivity} activityExercises={activityExercises}/>}
        </>
    )
}
