import React, { useState } from 'react'
import StratAndPauseButton from './StratAndPauseButton'
import Timer from '../Timer/Timer'
import { heatupAC } from "../../redux/actions/actions";
import { connect } from "react-redux";
import Heatup from './../Heatup/Heatup'
const mapStateToProps = (state) => ({
    state
});

function StartAndPauseContainer() {
    const [showExercise, setshowTimer] = useState(null)

    return (
        <div>
            {showExercise && <Heatup />}
            {showExercise && <Timer />}
            <StratAndPauseButton />
        </div>
    )
}

const mapDispatchToProps = dispatch => {
    return {
        heatup: (exercise) => { dispatch(heatupAC(exercise)) }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps,
)(StartAndPauseContainer);
