import React, { useState, useEffect } from 'react';

import Timer from '../Timer/Timer'
import Exercise from '../Exrcise/Exercise'
import './Heatup.css'

export default function Heatup(props) {


    useEffect(() => {
        props.setExerciseQuantity(heatupArray.length - 1)
    },[heatupArray.length]);

    useEffect(() => {
        props.exerciseNumber ? props.setHeatupExercise(heatupArray[props.exerciseNumber].name) : console.log('waiting')
    },[props.exerciseNumber]);

    return (
        <>  
            <div className="imgContainer">
                <Exercise />
                {props.exerciseNumber && <img src={heatupArray[props.exerciseNumber].img} alt='neck'></img>}
                <Timer />
            </div>
        </>
    )
}
