import React, { useState } from 'react'
import StratAndPauseButton from './StratAndPauseButton/StratAndPauseButton'
import Timer from './Timer/Timer'
import { heatupAC } from "../redux/actions/actions";
import { connect } from "react-redux";
import Heatup from './Heatup/Heatup'

const mapStateToProps = (state) => ({
    state
});

function StartAndPauseContainer() {
    const [showHeatup, setShowHeatup] = useState(null)
    const [showExercise, setShowExercise] = useState(null)
    const [startTimer, setStartTimer] = useState(null)

    return (
        <div>
            {showHeatup && <Heatup />}
            {startTimer && <Timer />}
            <StratAndPauseButton setStartTimer = {setStartTimer} setShowExercise={setShowExercise}/>
        </div>
    )
}

const mapDispatchToProps = dispatch => {
    return {
        heatup: (exercise) => { dispatch(heatupAC(exercise)) }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps,
)(StartAndPauseContainer);
